  useEffect(() => {
    console.log("names", names)
    setloading(false)
  }, [names])
  
  const addToArray = () => {
    var isValid = [];
    if (!name){
      setErrName("Name Required");
      isValid.push(false);
    } else {
      setErrName("");
      isValid.push(true);
    }
}  
    

